import React from "react";
import "./styles.css";
import cookie from "cookie";
import { Grommet, Box, Text, Button } from "grommet";

import CommandForm from "Command/Form";
import SessionForm from "Session/Form";

export default function App() {
  const [hasSession, setHasSession] = React.useState(false)

  const onSessionStart = () => {
    setHasSession(true);
  }

  const onSessionEnd = () => {
    setHasSession(false);
  }

  return (
    <Grommet
      theme={{
        global: {
          font: {
            family: "sans"
          }
        }
      }}
      full
    >
      <Box fill>
        <Box pad="medium" background="brand">
          HEADER{" "}
          {hasSession && <Button label="logout" onClick={onSessionEnd} />}
        </Box>
        <Box fill pad="medium" style={{ overflow: "auto" }}>
          {hasSession ? <CommandForm /> : <SessionForm onSessionStart={onSessionStart} />}
        </Box>
        <Box
          background="brand"
          pad={{ horizontal: "medium", vertical: "small" }}
        >
          <Text size="small">footer</Text>
        </Box>
      </Box>
    </Grommet>
  );
}
