import React from "react";
import "./styles.css";
import cookie from "cookie";
import { Grommet, Grid, Box, Text, Button } from "grommet";

import CommandForm from "Command/Form";
import SessionForm from "Session/Form";

export default function App() {
  const [hasSession, setHasSession] = React.useState(false);

  const onSessionStart = () => {
    setHasSession(true);
  };

  const onSessionEnd = () => {
    setHasSession(false);
  };

  return (
    <Grommet
      theme={{
        global: {
          font: {
            family: "sans"
          }
        }
      }}
      full
    >
      <Grid fill rows={["xsmall", "flex", "auto"]}>
        <Box align="center" direction="row" background="brand" gap="small">
          HEADER
          {hasSession && <Button label="logout" onClick={onSessionEnd} />}
        </Box>
        <Box fill pad="medium" overflow="auto">
          {hasSession ? (
            <CommandForm />
          ) : (
            <SessionForm onSessionStart={onSessionStart} />
          )}
        </Box>
        <Box
          background="brand"
          pad={{ horizontal: "medium", vertical: "small" }}
        >
          <Text size="small">footer</Text>
        </Box>
      </Grid>
    </Grommet>
  );
}
