import React from "react";
import { Box, Form, FormField, Input } from "grommet";
import { useQuery } from "react-query";
import { command } from "./api";

const CommandForm = () => {
  const { data, error, isFetching } = useQuery("command", command);

  return (
    <Box>
      <Form onSubmit={(e) => {}}>
        <FormField label="host" error={formik.errors.host}>
          <TextInput {...formik.getFieldProps("host")} disabled={isFetching} />
        </FormField>
        <Box pad="medium">
          <Button label="SEND" type="submit" disabled={isFetching} />
        </Box>
        {error && error}
      </Form>
    </Box>
  );
};

export default CommandForm;
