import React from "react";
import "./styles.css";
import cookie from "cookie";
import { Grommet, Box, Text } from "grommet";

import CommandForm from "Command/Form";
import SessionForm from "Session/Form";

export default function App() {
  const cookies = cookie.parse(document.cookie);
  console.log("cookies", cookies, document.cookie);
  const hasSession = false;
  const onSessionStart = () => {
    hasSession = true;
    console.log('a', cookie.parse(document.cookie))
  }
  return (
    <Grommet
      theme={{
        global: {
          font: {
            family: "sans"
          }
        }
      }}
      full
    >
      <Box fill>
        <Box pad="medium" background="brand">
          HEADER{" "}
        </Box>
        <Box fill pad="medium" style={{ overflow: "auto" }}>
          {hasSession ? <CommandForm /> : <SessionForm onSessionStart={onSessionStart} />}
        </Box>
        <Box
          background="brand"
          pad={{ horizontal: "medium", vertical: "small" }}
        >
          <Text size="small">footer</Text>
        </Box>
      </Box>
    </Grommet>
  );
}
