import React from "react";

import { Grid, Box, Spinner } from "grommet";
import { command } from "../api";
import { useMutation } from "react-query";

const StatusMonitor = (props) => {
  const { data, mutate, isLoading, isSuccess, isError } = useMutation(() =>
    command("status")
  );
  React.useEffect(() => {
    mutate();
    const interval = setInterval(mutate, 5000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  return (
    <Box>
      <Box direction="row">{isLoading && <Spinner size="xsmall" />}</Box>
      <Box>{data}</Box>
    </Box>
  );
};

export default StatusMonitor;
